#include <iostream>#include <vector>#include <algorithm>#include <random>using namespace std;class Code06_TrappingRainWater {public:};vector<int> getRandomArray(const int from, const int to, const int len) {    if (to < from || len < 1) {        return {};    }    random_device rd;    int range = to - from;    vector<int> ret(len, 0);    for (auto &n : ret) {        n = (double) rd() / rd.max() * range + from;    }    return ret;}void printArr(const vector<int> arr) {    if (arr.empty()) {        return;    }    for (const auto &n : arr) {        cout << n << " ";    }    cout << endl;}void play() {}int main(int argc, char *argv[]) {    play();    return 0;}